import { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { Directions } from './Directions'
import { FilterDirections } from './FilterDirections'
import { directions, filter } from '../currenciesData'
import { useActions } from '../store/useActions'
import revers_currency from '../imgs/reverse_currency.png'

function Currencies() {
    const { getCurrencies, filterCurrencies, filterCurrenciesDirection, reverses } = useActions()

    const directionsData = useSelector(state => state.currencies.directionsData)
    const filterData = useSelector(state => state.currencies.filterData)

    const [filterFrom, setFilterFrom] = useState("Bitcoin BTC")
    const [filterTo, setFilterTo] = useState("Наличные RUB")

    const [currenciesValue, setCurrenciesValue] = useState("")

    useEffect(() => {
        getCurrencies({ filter, directions })
    }, [])

    const handleReverseCurrency = () => {
        // setFilterFrom(filterTo)
        // setFilterTo(filterFrom)
        // console.log("filterFrom", filterFrom)
        // console.log("filterTo", filterTo)
        reverses()
    }

    return (
        <div>
            <Directions directionsData={directionsData}
                filterTo={filterTo}
                setFilterFrom={setFilterFrom}
                filterCurrenciesDirection={filterCurrenciesDirection}
                filterCurrencies={filterCurrencies}
                setCurrenciesValue={setCurrenciesValue}
            />

            <img src={revers_currency} alt="туда сюда" onClick={handleReverseCurrency}/>

            <FilterDirections filterData={filterData}
                filterFrom={filterFrom}
                filterCurrencies={filterCurrencies}
                currenciesValue={currenciesValue}
                setFilterTo={setFilterTo}
            />
        </div>
    )
}

export { Currencies }
