import { useEffect } from 'react'
import { useSelector } from 'react-redux'
import { Directions } from './Directions'
import { FilterDirections } from './FilterDirections'
import { directions, filter } from '../currenciesData'
import { useActions } from '../store/useActions'

function Currencies() {
    const { getCurrencies } = useActions()
    const directionsData = useSelector(state => state.currencies.directionsData)
    const filterData = useSelector(state => state.currencies.filterData)
    console.log("directionsData", directionsData)
    console.log("filterData", filterData)

    useEffect(() => {
        getCurrencies({ filter, directions })
    }, [])

    return (
        <div>
            <h4>Отдаете</h4>
            {directionsData.map(direction =>
                <Directions key={direction.code} directionName={direction.name}/>
            )}

            <h4>Получаете</h4>
            {filterData.map((filter, i) =>
                <FilterDirections key={i} filter={filter}/>
            )}
        </div>
    )
}

export { Currencies }
