import { createSlice } from "@reduxjs/toolkit"

const initialState = {
    directionsData: [],
    filterData: [],
    currenciesDirections: "",
    currenciesFilter: ""
}

const currenciesStore = createSlice({
    name: "currencies",
    initialState,
    reducers: {
        getCurrencies: (state, action) => {
            state.directionsData = action.payload.directions
            state.filterData = action.payload.filter
        },
        filterCurrenciesDirection: (state, action) => {
            state.currenciesDirections = action.payload.direction
            state.currenciesFilter = action.payload.filter

            // if(action.payload.directionFilter) {
            //     const { directionFilter } = action.payload
            //     state.directionsData = state.directionsData.filter(direction =>
            //         directionFilter.indexOf(direction.code) !== -1)
            // }


            // if(action.payload.filterCurr)
            //     state.filterData = state.filterData.filter(direction => direction.name)
        },

    }
})

export const { reducer: currenciesReducer } = currenciesStore
export const { getCurrencies, filterCurrenciesDirection } = currenciesStore.actions
