import { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { Directions } from './Directions'
import { FilterDirections } from './FilterDirections'
import { directions, filter } from '../currenciesData'
import { useActions } from '../store/useActions'

function Currencies() {
    const { getCurrencies, filterCurrencies } = useActions()
    const directionsData = useSelector(state => state.currencies.directionsData)
    const filterData = useSelector(state => state.currencies.filterData)
    const currenciesDirections = useSelector(state => state.currencies.currenciesDirections)
    const currenciesFilter = useSelector(state => state.currencies.currenciesFilter)
    const [filterFrom, setFilterFrom] = useState("")
    // const [currenciesDirections, setCurrenciesDirections] = useState("")
    // const [currenciesFilter, setCurrenciesFilter] = useState("")

    useEffect(() => {
        getCurrencies({ filter, directions })
    }, [])

    useEffect(() => {
        filterCurrencies({ filter: "" })
    }, [filterFrom])

    return (
        <div>
            <Directions directionsData={directionsData}
                setFilterFrom={setFilterFrom}
                currenciesDirections={currenciesDirections}/* setCurrenciesDirections={setCurrenciesDirections}*//>
            <FilterDirections filterData={filterData}
                filterFrom={filterFrom}
                currenciesFilter={currenciesFilter} /*setCurrenciesFilter={setCurrenciesFilter}*/ />
        </div>
    )
}

export { Currencies }
