import { useEffect, useState } from 'react'
import styles from '../css/currencies_styles.module.scss'
import { useSelector } from 'react-redux'

function FilterDirections({ filterData, filterFrom, currenciesFilter, filterCurrencies }) {
  const [filterCurr, setFilterCurr] = useState(filterData)
  const [currenciesFilterFrom, setCurrenciesFilterFrom] = useState(filterCurr)
  const currenciesDirections = useSelector(state => state.currencies.currenciesDirections)

  useEffect(() => {
    setFilterCurr(filterData)
  }, [filterData])

  useEffect(() => {
    switch(currenciesFilter) {
      case "crypto":
        setFilterCurr(filterData.filter(d => ["BTC", "ETH", "USDTTRC"].indexOf(d.from.code) !== -1))
        break
      case "cash":
        setFilterCurr(filterData.filter(d => ["CASHUSD", "CASHRUB"].indexOf(d.from.code) !== -1))
        break
      case "banksRUB":
      case "banksUAH":
        setFilterCurr(filterData.filter(d => ["ACRUB", "SBERRUB", "TCSBRUB"].indexOf(d.from.code) !== -1))
        break
      default:
        setFilterCurr(filterData)
    }
  }, [currenciesFilter, filterData, filterFrom])

  useEffect(() => {
      setCurrenciesFilterFrom(
        filterCurr.filter(d => d.from.name.includes(filterFrom))
      )
  }, [currenciesFilter, filterFrom, filterCurr, currenciesDirections])

  return (
    <div className={styles.filter}>
       <div className={styles.header}>Получаете</div>
        <div className={styles.filterCurr}>
            <button onClick={() =>
              filterCurrencies("")} className={currenciesFilter === "" ? styles.selectFilter : ``}>Все</button>
            <button onClick={() =>
              filterCurrencies("crypto")} className={currenciesFilter === "crypto" ? styles.selectFilter : ``}>Криптовалюты</button>
            <button onClick={() =>
              filterCurrencies("cash")} className={currenciesFilter === "cash" ? styles.selectFilter : ``}>Наличные</button>
            <button onClick={() =>
              filterCurrencies("banksRUB")} className={currenciesFilter === "banksRUB" ? styles.selectFilter : ``}>Банки RUB</button>
            <button onClick={() =>
              filterCurrencies("banksUAH")} className={currenciesFilter === "banksUAH" ? styles.selectFilter : ``}>Банки UAH</button>
        </div>

        <div>
          <input type="text" />

          <select>

            {currenciesFilterFrom.map(filterFrom =>
              filterFrom.to.map((filter, i) =>
                <option key={i}>{filter.name}</option>)
            )}
            
          </select>
        </div>
    </div>
  )
}

export { FilterDirections }
