import { useEffect, useState } from 'react'
import styles from '../css/currencies_styles.module.scss'

function FilterDirections({ filterData, filterFrom }) {
  const [filterCurrencies, setFilterCurrencies] = useState(filterData)
  const [currenciesFilterFrom, setCurrenciesFilterFrom] = useState(filterCurrencies)
  const [currVal, setCurrVal] = useState("")
  // console.log("filterData", filterData)
  useEffect(() => {
    setFilterCurrencies(filterData)
  }, [filterData])

  useEffect(() => {
    switch(currVal) {
      case "crypto":
        setFilterCurrencies(filterData.filter(d => ["BTC", "ETH", "USDTTRC"].indexOf(d.from.code) !== -1))
        break
      case "cash":
        setFilterCurrencies(filterData.filter(d => ["CASHUSD", "CASHRUB"].indexOf(d.from.code) !== -1))
        break
      case "banksRUB":
      case "banksUAH":
        setFilterCurrencies(filterData.filter(d => ["ACRUB", "SBERRUB", "TCSBRUB"].indexOf(d.from.code) !== -1))
        break
      default:
        setFilterCurrencies(filterData)
    }
  }, [currVal])

  useEffect(() => {
      setCurrenciesFilterFrom(filterCurrencies.filter(d => d.from.name.includes(filterFrom)))
      console.log("currenciesFilterFrom", currenciesFilterFrom)
      console.log("filterFrom", filterFrom)
  }, [currVal, filterFrom, filterCurrencies])


  return (
    <div className={styles.filter}>
       <div className={styles.header}>Получаете</div>
        <div className={styles.filterCurr}>
            <button onClick={() => setCurrVal("")} className={currVal === "" ? styles.selectFilter : ``}>Все</button>
            <button onClick={() => setCurrVal("crypto")} className={currVal === "crypto" ? styles.selectFilter : ``}>Криптовалюты</button>
            <button onClick={() => setCurrVal("cash")} className={currVal === "cash" ? styles.selectFilter : ``}>Наличные</button>
            <button onClick={() => setCurrVal("banksRUB")} className={currVal === "banksRUB" ? styles.selectFilter : ``}>Банки RUB</button>
            <button onClick={() => setCurrVal("banksUAH")} className={currVal === "banksUAH" ? styles.selectFilter : ``}>Банки UAH</button>
        </div>

        <div>
          <input type="text" />

          <select>
            {currenciesFilterFrom.map(filterFrom =>
              filterFrom["to"].map((filter, i) =>
                <option key={i}>{filter.name}</option>)
            )}
          </select>
        </div>
    </div>
  )
}

export { FilterDirections }
