import { useEffect, useRef, useState } from 'react'
import styles from '../css/currencies_styles.module.scss'
import { useActions } from '../store/useActions'

function Directions({ directionsData }) {
const [dirCurrencies, setDirCurrencies] = useState(directionsData)

useEffect(() => {
  setDirCurrencies(directionsData)
}, [directionsData])

const filterCash = (cash) => {
  return () => {
    if(cash === "crypto")
      setDirCurrencies(directionsData.filter(d => ["BTC", "ETH", "USDTTRC"].indexOf(d.code) !== -1))
    
    if(cash === "cash") 
      setDirCurrencies(directionsData.filter(d => ["CASHUSD", "CASHRUB"].indexOf(d.code) !== -1))

    if(cash === "banks") 
      setDirCurrencies(directionsData.filter(d => ["ACRUB", "SBERRUB", "TCSBRUB"].indexOf(d.code) !== -1))

  }

}

  return (
    <>
       <h4>Отдаете</h4>
        <div className={styles.filterCurr}>
            <button onClick={filterCash("")}>Все</button>
            <button onClick={filterCash("crypto")}>Криптовалюты</button>
            <button onClick={filterCash("cash")}>Наличные</button>
            <button onClick={filterCash("banks")}>Банки RUB</button>
            <button onClick={filterCash("banks")}>Банки UAH</button>
        </div>

        {dirCurrencies.map((direction, i) => (
          <div key={i}>
            <span>{direction.name}</span>
          </div>
        ))}
    </>
  )
}

export { Directions }
