import { directions, filter } from '../currenciesData'
import { useActions } from '../store/useActions'
import styles from '../css/currencies_styles.module.scss'

function Currencies() {
    const [currencies, setCurrencies] = useState("")
    const { getCurrencies } = useActions()
    const directionsData = useSelector(state => state.currencies.directionsData)
    const filterData = useSelector(state => state.currencies.filterData)
    console.log("directionsData", directionsData)
    console.log("filterData", filterData)

    useEffect(() => {
        getCurrencies({ filter, directions })
    }, [])

    const filterCash = (cash) => {
        return () => {
            setCurrencies(cash)
        }
    }

    return (
        <div>
            <h4>Отдаете</h4>
            <div className={styles.filterCurr}>
                <button onClick={() => setCurrencies("")}>Все</button>
                <button onClick={() => setCurrencies("crypto")}>Криптовалюты</button>
                <button onClick={() => setCurrencies("cash")}>Наличные</button>
                <button onClick={() => setCurrencies("banksRUB")}>Банки RUB</button>
                <button onClick={() => setCurrencies("banksUAH")}>Банки UAH</button>
            </div>
            {/* {directionsData.map(direction =>
