import { createSlice } from "@reduxjs/toolkit"

const initialState = {
    directionsData: [],
    filterData: []
}

const currenciesStore = createSlice({
    name: "currencies",
    initialState,
    reducers: {
        getCurrencies: (state, action) => {
            state.directionsData = action.payload.directions
            state.filterData = action.payload.filter
        },
        filterCurrencies: (state, action) => {
            if(action.payload.directionFilter) {
                const { directionFilter } = action.payload
                console.log("directionFilter", directionFilter)
                const newArray = [...state.directionsData]
                state.directionsData = newArray.filter(direction =>
                    directionFilter.indexOf(direction.code) !== -1)
            }


            if(action.payload.filterCurr)
                state.filterData = state.filterData.filter(direction => direction.name)
        }
    }
})

export const { reducer: currenciesReducer } = currenciesStore
export const { getCurrencies, filterCurrencies } = currenciesStore.actions
