import { useRef, useState } from 'react'
import styles from '../css/currencies_styles.module.scss'
import { useActions } from '../store/useActions'

function Directions({ directionsData }) {
  const [currencies, setCurrencies] = useState([])
  const { filterCurrencies } = useActions()
  const currenciesRef = useRef([])
  
  const filterCash = (cash) => {
    return () => {
      if(cash === "crypto")
      currenciesRef.current = ["BTC", "ETH", "USDTTRC"]
        // setCurrencies(["BTC", "ETH", "USDTTRC"])
      
      if(cash === "cash") 
      currenciesRef.current = ["CASHUSD", "CASHRUB"]
        // setCurrencies(["CASHUSD", "CASHRUB"])

      if(cash === "banks") 
      currenciesRef.current = ["ACRUB", "SBERRUB", "TCSBRUB"]
        // setCurrencies(["ACRUB", "SBERRUB", "TCSBRUB"])

      console.log(currencies)
        
      filterCurrencies({ directionFilter: currenciesRef.current })
    }
}

  return (
    <>
       <h4>Отдаете</h4>
        <div className={styles.filterCurr}>
            <button onClick={filterCash("")}>Все</button>
            <button onClick={filterCash("crypto")}>Криптовалюты</button>
            <button onClick={filterCash("cash")}>Наличные</button>
            <button onClick={filterCash("banks")}>Банки RUB</button>
            <button onClick={filterCash("banks")}>Банки UAH</button>
        </div>
        {directionsData.map(direction => (
          <div key={direction.code}>
            <span>{direction.name}</span>
          </div>
        ))}
    </>
  )
}

export { Directions }
