import { memo, useEffect, useState } from 'react'
import styles from '../css/currencies_styles.module.scss'

const Directions = memo(function Directions({ directionsData, setFilterFrom }) {
  const [dirCurrencies, setDirCurrencies] = useState(directionsData)
  const [changeCurrencies, setChangeCurrencies] = useState("Bitcoin")
  const [currVal, setCurrVal] = useState("")

  useEffect(() => {
    setDirCurrencies(directionsData)
  }, [directionsData])

  useEffect(() => {
    switch(currVal) {
      case "crypto":
        setDirCurrencies(directionsData.filter(d => ["BTC", "ETH", "USDTTRC"].indexOf(d.code) !== -1))
        break
      case "cash":
        setDirCurrencies(directionsData.filter(d => ["CASHUSD", "CASHRUB"].indexOf(d.code) !== -1))
        break
      case "banksRUB":
      case "banksUAH":
        setDirCurrencies(directionsData.filter(d => ["ACRUB", "SBERRUB", "TCSBRUB"].indexOf(d.code) !== -1))
        break
      default:
        setDirCurrencies(directionsData)
    }
  }, [currVal])
  console.log("Directions render")
  useEffect(() => {
    setFilterFrom(changeCurrencies)
  }, [changeCurrencies, currVal])

  return (
    <>
       <div className={styles.header}>Отдаете</div>
        <div className={styles.filterCurr}>
            <button onClick={() =>
              setCurrVal("")} className={currVal === "" ? styles.selectFilter : ``}>Все</button>
            <button onClick={() =>
              setCurrVal("crypto")} className={currVal === "crypto" ? styles.selectFilter : ``}>Криптовалюты</button>
            <button onClick={() =>
              setCurrVal("cash")} className={currVal === "cash" ? styles.selectFilter : ``}>Наличные</button>
            <button onClick={() =>
              setCurrVal("banksRUB")} className={currVal === "banksRUB" ? styles.selectFilter : ``}>Банки RUB</button>
            <button onClick={() =>
              setCurrVal("banksUAH")} className={currVal === "banksUAH" ? styles.selectFilter : ``}>Банки UAH</button>
        </div>

        <div>
          <input type="text" />

          <select onChange={(e) =>
            setChangeCurrencies(e.target.options[e.target.selectedIndex].value)}>

            {dirCurrencies.map((direction, i) => (
                <option key={i} value={direction.name}>{direction.name}</option>
            ))}

          </select>
        </div>
    </>
  )
})

export { Directions }
